import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ListGroup, Tab } from 'react-bootstrap';
import Wrapper from '../components/Wrapper';
import RestaurantResult from '../components/RestaurantResult';
import { locations } from '../assets/datas/locations';

const FoodPage = () => {
  const [choice, setChoice] = useState({
    complete: false,
    location: {
      en_name: '',
      kor_name: '',
      shortcut: ''
    }
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    console.log('choice: ', choice);
  }, [choice]);

  const handleSelect = item => {
    console.log(item);
    setChoice({
      ...choice,
      location: {
        en_name: item.en_name,
        kor_name: item.kor_name,
        shortcut: item.shortcut
      }
    });
  };

  const handleSubmit = () => {
    // 선택 완료 상태로 만들기
    setChoice({
      ...choice,
      complete: true
    });
    // 로딩 상태 만들기
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  };

  return (
    <Wrapper>
      <FoodPageBlock>
        <div className='header'>
          <div className='header-title'>음식점 랜덤 선택하기</div>
        </div>
        <div className='content'>
          <ListGroup className='content-list'>
            {locations.map(item => (
              <ListGroup.Item
                className='item'
                onClick={() => handleSelect(item)}
                key={item.id}
                item={item}
                action
              >
                {item.kor_name}
              </ListGroup.Item>
            ))}
          </ListGroup>
          <div className='content-result'>
            {loading && choice.complete && <div>로딩중</div>}
            {!loading && !choice.complete && (
              <div className='location'>{choice.location.kor_name}</div>
            )}
            {!loading && choice.complete && <RestaurantResult />}
          </div>
          <div className='content-button' onClick={handleSubmit}>
            랜덤 선택
          </div>
        </div>
        <Link to='/choice' style={{ textDecoration: 'none', color: 'black' }}>
          <div className='footer'>
            <div className='footer-content'>선택 페이지로 가기</div>
          </div>
        </Link>
      </FoodPageBlock>
    </Wrapper>
  );
};

export default FoodPage;

const FoodPageBlock = styled.div`
  width: 100%;
  height: 100%;

  .header {
    height: 13%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .header-title {
      display: inline-block;
      font-size: 30px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }

  .content {
    height: 77%;
    display: grid;
    grid-template-columns: 1fr 1fr;
    grid-template-rows: 5fr 1fr;
  }

  .content-list {
    overflow: auto;
    height: 100%;
    grid-column: 1/2;
    grid-row: 1/6;
    .item {
      text-align: center;
    }
  }
  .content-result {
    background-color: yellow;
    grid-column: 2/2;
    margin: 10px;
    .location {
      text-align: center;
      margin-top: 100px;
    }
  }
  .content-button {
    background-color: #dee2e6;
    cursor: pointer;
    grid-column: 2/2;
    margin: 10px;
    padding-top: 10px;
    text-align: center;
    font-size: 20px;
    letter-spacing: 5px;
    text-indent: 5px;
    border-bottom: 5px solid rgba(0, 0, 0, 0.2);

    :active {
      border-bottom: 0;
      margin-top: 15px;
    }
  }

  .footer {
    height: 10%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .footer-content {
      display: inline-block;
      font-size: 20px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }
`;
