import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ListGroup } from 'react-bootstrap';
import Wrapper from '../components/Wrapper';
import DomesticResult from '../components/DomesticResult';
import { domesticPlaces } from '../assets/datas/domesticPlaces';

const DomesticPage = () => {
  const [choice, setChoice] = useState({
    complete: false,
    region: ''
  });

  useEffect(() => {
    console.log('choice', choice);
  }, [choice]);

  const handleSelect = item => {
    setChoice({
      ...choice,
      region: item.region
    });
  };

  const handleSubmit = () => {
    if (choice.region === '') {
      return;
    }
    setChoice({
      ...choice,
      complete: true
    });
  };

  const handleReSubmir = () => {
    setChoice({
      complete: false,
      region: ''
    });
  };

  return (
    <Wrapper>
      <DomesticPageBlock>
        <div className='header'>
          <div className='header-title'>국내여행지 랜덤 선택하기</div>
        </div>
        <div className='content'>...</div>
        <Link to='/choice' style={{ textDecoration: 'none', color: 'black' }}>
          <div className='footer'>
            <div className='footer-content'>선택 페이지로 가기</div>
          </div>
        </Link>
      </DomesticPageBlock>
    </Wrapper>
  );
};

export default DomesticPage;

const DomesticPageBlock = styled.div`
  width: 100%;
  height: 100%;

  .header {
    height: 13%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .header-title {
      display: inline-block;
      font-size: 30px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }

  .content {
    height: 77%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .footer {
    height: 10%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .footer-content {
      display: inline-block;
      font-size: 20px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }
`;
