import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { ListGroup } from 'react-bootstrap';
import Wrapper from '../components/Wrapper';
import ForeignResult from '../components/ForeignResult';
import { regions } from '../assets/datas/foreignRegions';

const ForeignPage = () => {
  const [choice, setChoice] = useState({
    complete: false,
    region: '',
    regionKorean: ''
  });

  const handleSelect = item => {};
  return (
    <Wrapper>
      <ForeignPageBlock>
        <div className='header'>
          <div className='header-title'>해외여행지 랜덤 선택하기</div>
        </div>
        <div className='content'>
          <ListGroup className='content-list'>
            {regions.map(item => (
              <ListGroup.Item
                className='item'
                onClick={() => handleSelect(item)}
                key={item.id}
                item={item}
                action
              >
                {item.kor}
              </ListGroup.Item>
            ))}
          </ListGroup>
          <div className='content-result'>
            {!choice.complete && choice.location.en_name === '' && (
              <div className='location-default'>지역을 선택하세요</div>
            )}
            {!choice.complete && choice.location.en_name !== '' && (
              <div className='location'>
                <b>{choice.location.kor_name}</b>를
                <br />
                선택하셨습니다
              </div>
            )}
            {choice.complete && <RestaurantResult location={choice.location} />}
          </div>
          {!choice.complete ? (
            <div className='content-button' onClick={handleSubmit}>
              맛집을 선택해줄게요
            </div>
          ) : (
            <div className='content-button' onClick={handleReSubmit}>
              재선택하기
            </div>
          )}
        </div>
        <Link to='/choice' style={{ textDecoration: 'none', color: 'black' }}>
          <div className='footer'>
            <div className='footer-content'>선택 페이지로 가기</div>
          </div>
        </Link>
      </ForeignPageBlock>
    </Wrapper>
  );
};

export default ForeignPage;

const ForeignPageBlock = styled.div`
  width: 100%;
  height: 100%;

  .header {
    height: 13%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .header-title {
      display: inline-block;
      font-size: 30px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }

  .content {
    height: 77%;
    display: flex;
    justify-content: center;
    align-items: center;
  }

  .footer {
    height: 10%;
    background-color: #51cf66;
    display: flex;
    justify-content: center;
    align-items: center;
    .footer-content {
      display: inline-block;
      font-size: 20px;
      font-weight: 1000;
      letter-spacing: 5px;
      text-indent: 5px;
    }
  }
`;
