import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styled from 'styled-components';
import Loading from '../components/Loading';

function getRandomNumber(max) {
  const rand = Math.floor(Math.random() * max);
  return rand;
}

const RestaurantResult = ({ location }) => {
  const url = `http://openapi.${location.en_name}:8088/644f5065566962793636474968446e/json/${location.shortcut}ModelRestaurantDesignate/1/100/`;

  const [info, setInfo] = useState({
    name: '',
    addr: '',
    main_menu: ''
  });

  const [loading, setLoading] = useState(false);

  useEffect(() => {
    axios
      .get(url)
      .then(result => {
        setLoading(true);

        const idx = getRandomNumber(100);
        const data =
          result.data[`${location.shortcut}ModelRestaurantDesignate`].row[idx];
        console.log(data);
        setInfo({
          name: data.UPSO_NM,
          addr: data.SITE_ADDR_RD,
          main_menu: data.MAIN_EDF
        });

        setLoading(false);
      })
      .catch(e => console.log(e));
  }, []);

  return (
    <ResultBlock>
      {loading && <Loading />}
      <div className='name'>
        <b>이름</b>
        {info.name}
      </div>
      <div className='address'>
        <b>주소</b>
        {info.addr}
      </div>
      <div className='mainMenu'>
        <b>메인 요리</b>
        {info.main_menu}
      </div>
    </ResultBlock>
  );
};

const ResultBlock = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column .name {
    margin-top: 50px;
  }
`;

export default RestaurantResult;
